<h3>Orders</h3>
<?php $orders=$pdo->query("SELECT * FROM orders ORDER BY created_at DESC")->fetchAll(); foreach($orders as $o){ echo "<div class='card mb-2'><div class='card-body'><h5>Order #{$o['id']} - ".esc($o['status'])."</h5><p>{$o['created_at']} | Total Rp ".number_format($o['total'],0,',','.')."</p></div></div>"; } ?>